def playerToFN(p):
	return p.lower().replace(".","").replace(" ","_")+".jpg"

#returns dict of {player : image_filename}
def genPlayerImageDict(players):
	return dict(zip(players, map(playerToFN,players)))

def kMeans(img,k):
	#convert image to list of rgb pixel values
	pil_img= img.load()
	width= img.size[0]
	height= img.size[1]
	pixels= []
	for i in range(width):
		for j in range(height):
			pixels.append(list(pil_img[i,j]))
	pixels= np.array(pixels)

	#perform kMeans algorithm
	centroids,labels= kmeans2(pixels,k)
	centroids= [[int(x) for x in vector] for vector in centroids]
	
	#convert kMeans results into new image
	for i in range(width):
		for j in range(height):
			pil_img[i,j]= tuple(centroids[labels[i*height+j]])
	return img

#list of colors from darkest to lightest
def getBrightestColors(img):
	pil_img= img.load()
	width= img.size[0]
	height= img.size[1]
	colors= set([])

	for i in range(width):
		for j in range(height):
			colors.add(pil_img[i,j])

	#standard formula for luminance is (0.2126*R) + (0.7152*G) + (0.0722*B). 0=dark, 255=bright
	colors= sorted(list(colors), key=lambda c: (0.2126*c[0]) + (0.7152*c[1]) + (0.0722*c[2])) 
	return colors

def hex_to_rgb(value):
    value = value.lstrip('#')
    lv = len(value)
    return tuple(int(value[i:i + lv // 3], 16) for i in range(0, lv, lv // 3))

def colorChangeBright(player,team,folder,img):	
	new=  map(hex_to_rgb,[teamToColor[team]["dark"],teamToColor[team]["light"],background])
	old= getBrightestColors(img)

	pil_img= img.load()
	width= img.size[0]
	height= img.size[1]
	for i in range(width):
		for j in range(height):
			if pil_img[i,j]==old[0]:
				pil_img[i,j]= new[0]
			elif pil_img[i,j]==old[1]:
				pil_img[i,j]= new[1]
			else:
				pil_img[i,j]= new[2]

	img.save(folder+'/mods/'+playerToFN(player), "JPEG", quality=80, optimize=True, progressive=True)


